import BusinessPartnerDialog from "./BusinessPartnerDialog.vue";
import SupplierDialog from "./SupplierDialog.vue";
import CustomerDialog from "./CustomerDialog.vue";
import DepartmentDialog from "./DepartmentDialog.vue";
import CountryDialog from "./CountryDialog.vue";

export default {
  name: "AppLayout",
  components: {
    BusinessPartnerDialog,
    SupplierDialog,
    CustomerDialog,
    DepartmentDialog,
    CountryDialog,
  },
  data() {
    return {
      isDropdownVisible: false,
      isBusinessPartnerDialogOpen: false,
      isSupplierDialogOpen: false,
      isCustomerDialogOpen: false,
      isDepartmentDialogOpen: false,
      isCountryDialogOpen: false,
      businessPartnerDialogKey: 0,
      supplierDialogKey: 0,
      customerDialogKey: 0,
      departmentDialogKey: 0,
      countryDialogKey: 0,
    };
  },
  methods: {
    toggleDropdown() {
      this.isDropdownVisible = !this.isDropdownVisible;
    },
    openBusinessPartnerDialog() {
      this.isBusinessPartnerDialogOpen = true;
      this.isDropdownVisible = true;
    },
    openSupplierDialog() {
      this.isSupplierDialogOpen = true;
      this.isDropdownVisible = true;
    },
    openCustomerDialog() {
      this.isCustomerDialogOpen = true;
      this.isDropdownVisible = true;
    },
    openDepartmentDialog() {
      this.isDepartmentDialogOpen = true;
      this.isDropdownVisible = true;
    },
    openCountryDialog() {
      this.isCountryDialogOpen = true;
      this.isDropdownVisible = true;
    },
    closeBusinessPartnerDialog() {
      this.isBusinessPartnerDialogOpen = false;
      this.businessPartnerDialogKey += 1;
    },
    closeSupplierDialog() {
      this.isSupplierDialogOpen = false;
      this.supplierDialogKey += 1;
    },
    closeCustomerDialog() {
      this.isCustomerDialogOpen = false;
      this.customerDialogKey += 1;
    },
    closeDepartmentDialog() {
      this.isDepartmentDialogOpen = false;
      this.departmentDialogKey += 1;
    },
    closeCountryDialog() {
      this.isCountryDialogOpen = false;
      this.countryDialogKey += 1;
    },
  },
};
