export default {
    data() {
        return {
            isDialogVisible: true,
            searchType: "code",
            matchType: "prefix",
            searchData: "",
            results: [
                { code: "000088", name: "User 1" },
                { code: "100099", name: "User 2" },
                { code: "400100", name: "User 3" },
                { code: "300101", name: "User 4" },
                { code: "600100", name: "User 3" },
                { code: "000099", name: "User 2" },
                { code: "200100", name: "User 3" },
                { code: "000101", name: "User 4" },
            ],
            searchedResults: [], 
        };
    },
    mounted() {
        // Initially display all results in the table
        this.searchedResults = this.results;
    },
    methods: {
        onSearch() {
            const data = this.searchData.trim().toLowerCase(); // Clean and lowercase the input
            if (!data) {
                // If the search data is empty, reset the results to show all data
                this.searchedResults = this.results;
                return;
            }

            // Filter based on search type and match type
            this.searchedResults = this.results.filter((item) => {
                const searchField =
                    this.searchType === "code" ? item.code : item.name; // Field to search on
                const lowerSearchField = searchField.toLowerCase();

                // Matching logic
                if (this.matchType === "prefix") {
                    return lowerSearchField.startsWith(data); // Check if it starts with the query
                } else if (this.matchType === "partial") {
                    return lowerSearchField.includes(data); // Check if it contains the query
                }
                return false;
            });

            console.log("Search results:", this.searchedResults);
        },
        onClear() {
            this.searchData = "";
            this.searchedResults = this.results; // Reset search results to all data
        },
        onCancel() {
            this.isDialogVisible = false;
        },
    },
};
