                { code: 12, shortName: "User", fullName: "Five" },
            ],
            currentPage: 1,
            itemsPerPage: 15,
            menuVisible: false,
            menuPosition: { x: 0, y: 0 },
            dialogVisible: null,
            selectedCompany: null,
            selectedCompanyIndex: -1, // to track index of selected company in the full list
        };
    },
    computed: {
        totalPages() {
            return Math.ceil(this.companies.length / this.itemsPerPage);
        },
        paginatedCompanies() {
            const start = (this.currentPage - 1) * this.itemsPerPage;
            const end = start + this.itemsPerPage;
            return this.companies.slice(start, end);
        },
        dataRange() {
            const start = (this.currentPage - 1) * this.itemsPerPage + 1;
            const end = Math.min(this.currentPage * this.itemsPerPage, this.companies.length);
            return `${start} - ${end}`;
        },
        totalData() {
            return this.companies.length;
        }
    },
    methods: {
        previousPage() {
            if (this.currentPage > 1) {
                this.currentPage--;
            }
        },
        nextPage() {
            if (this.currentPage < this.totalPages) {
                this.currentPage++;
            }
        },
        showMenu(index, event) {
            this.menuVisible = true;
            this.menuPosition = { x: event.clientX, y: event.clientY };
            this.selectedCompanyIndex = (this.currentPage - 1) * this.itemsPerPage + index; // adjust index based on pagination
            this.selectedCompany = this.companies[this.selectedCompanyIndex]; // select the correct company
        },
        openDialog(action, company) {
            this.dialogVisible = action;
            this.selectedCompany = { ...company };
            if (action === "edit") {
                this.selectedCompany.fullName = ""; // Clear full name for editing
            }
            this.menuVisible = false;
        },
        closeDialog() {
            this.dialogVisible = null;
        },
        updateEdit() {
            const company = this.companies[this.selectedCompanyIndex]; // find the company using the correct index
            if (company && this.selectedCompany.fullName.trim()) {
                company.fullName = this.selectedCompany.fullName.trim();
            }
            this.closeDialog();
        },
        confirmDelete() {
            this.companies = this.companies.filter((c) => c.code !== this.selectedCompany.code);
            this.closeDialog();
        },
        handleClickOutside(event) {
            const contextMenu = this.$el.querySelector('.context-menu');
            const shippingCompanyCells = this.$el.querySelectorAll('.shippingComapnyNameColumn');

            if (contextMenu && !contextMenu.contains(event.target) && !Array.from(shippingCompanyCells).some(cell => cell.contains(event.target))) {
                this.menuVisible = false;
            }
        }
    },
    mounted() {
        document.addEventListener('click', this.handleClickOutside);
    },
    beforeUnmount() {
        document.removeEventListener('click', this.handleClickOutside);
    }
